package Praktikum4;

public class Jadad {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 0; i < 10; i++) {
			System.out.print((10-i) + " ");
			
		}
		System.out.println();
		
		for (int i = 0; i <= 10; i=i+2) {
			System.out.print(i + " ");
		}
		System.out.println();
		for (int i=30; i>0; i=i-3) {
			System.out.print(i + " ");
		}
	}

}
